mapper.select:
select j.*,c.cName cname,c.address address from rec_company c inner join rec_job j on c.cid = j.cid


        and j.jname like CONCAT('%',#{jname},'%')


        and c.address like concat('%',#{address},'%')


        and putDate > DATE_FORMAT(DATE_SUB(NOW(),INTERVAL #{day} DAY),'%Y-%m-%d')



            and payment>#{min} and payment<=#{max}


            and payment >50



        and natureid = #{natureid}



            and yearsReq >=#{minyears} and yearsReq <#{maxyears}


            and yearsReq>#{minyears}


            and yearsReq = 0



        and educationReq = #{education}



            and scale >#{minscale}


            and scale >5000
